// Process Groq response and create text chunks
const fileData = $('Prepare File Path').first().json;
const markdownContent = $('LlamaParse Premium').first().json;
const groqResponse = $('Groq AI Entity Extraction').first().json;

// Extract AI results from Groq response
let extractedData = { entities: [], relationships: [], structured_elements: {} };
try {
  if (groqResponse.choices && groqResponse.choices[0] && groqResponse.choices[0].message) {
    const content = groqResponse.choices[0].message.content;
    extractedData = JSON.parse(content);
  }
} catch (error) {
  console.log('Error parsing Groq response:', error.message);
  console.log('Raw Groq response:', JSON.stringify(groqResponse, null, 2));
}

// Create systematic text chunks
function createSystematicChunks(text, chunkSize = 300, overlap = 75) {
  if (!text || typeof text !== 'string') {
    return [];
  }

  const words = text.split(/\s+/);
  const chunks = [];

  for (let i = 0; i < words.length; i += (chunkSize - overlap)) {
    const chunk = words.slice(i, i + chunkSize).join(' ');
    if (chunk.trim().length > 50) {
      chunks.push({
        id: `chunk_${chunks.length + 1}`,
        content: chunk,
        word_count: chunk.split(/\s+/).length,
        start_word: i,
        end_word: Math.min(i + chunkSize, words.length),
        metadata: {
          document: fileData.filename,
          chunk_index: chunks.length,
          processing_timestamp: new Date().toISOString()
        }
      });
    }
  }

  return chunks;
}

const textChunks = createSystematicChunks(markdownContent);

return [{
  json: {
    batch_id: fileData.batch_id,
    filename: fileData.filename,
    total_chunks: textChunks.length,
    chunks: textChunks,
    extracted_data: extractedData,
    groq_response: groqResponse,
    document_info: {
      title: fileData.filename.replace('.pdf', ''),
      type: 'research_paper',
      file_path: fileData.file_path,
      processed_by: 'fixed_groq_workflow',
      processing_timestamp: new Date().toISOString(),
      llm_used: 'groq_llama_3_1_70b_via_http'
    }
  }
}];

parameters.jsonBody:
={{ 
  const processedData = $('Process Results & Create Chunks').first().json;
  const extractedData = processedData.extracted_data;
  const documentInfo = processedData.document_info;

  const statements = [];

  // Create document node
  statements.push({
    statement: "MERGE (doc:Document {id: $doc_id}) SET doc.title = $title, doc.type = $type, doc.file_path = $file_path, doc.processed_at = datetime($processed_at), doc.processed_by = $processed_by, doc.llm_used = $llm_used RETURN doc.id as document_id",
    parameters: {
      doc_id: documentInfo.title.replace(/\s+/g, '_').toLowerCase(),
      title: documentInfo.title,
      type: documentInfo.type,
      file_path: documentInfo.file_path,
      processed_at: documentInfo.processing_timestamp,
      processed_by: documentInfo.processed_by,
      llm_used: documentInfo.llm_used
    }
  });

  // Create entity nodes
  if (extractedData.entities && Array.isArray(extractedData.entities)) {
    extractedData.entities.forEach((entity, index) => {
      statements.push({
        statement: "MERGE (e:Entity {id: $entity_id}) SET e.name = $name, e.type = $entity_type, e.description = $description, e.confidence = $confidence RETURN e.id as entity_id",
        parameters: {
          entity_id: entity.id || `entity_${index}`,
          name: entity.name || 'Unknown',
          entity_type: entity.type || 'concept',
          description: entity.description || '',
          confidence: entity.confidence || 0.8
        }
      });
    });
  }

  // Create relationship edges
  if (extractedData.relationships && Array.isArray(extractedData.relationships)) {
    extractedData.relationships.forEach((rel, index) => {
      statements.push({
        statement: "MATCH (source:Entity {id: $source_id}), (target:Entity {id: $target_id}) MERGE (source)-[r:RELATED {type: $rel_type}]->(target) SET r.context = $context, r.confidence = $confidence RETURN r",
        parameters: {
          source_id: rel.source_entity || `entity_${index}_source`,
          target_id: rel.target_entity || `entity_${index}_target`,
          rel_type: rel.relationship_type || 'RELATED_TO',
          context: rel.context || '',
          confidence: rel.confidence || 0.8
        }
      });
    });
  }

  return { statements: statements };
}}

={{ 
  const processedData = $('Process Results & Create Chunks').first().json;
  const collectionName = processedData.document_info.title.replace(/\s+/g, '_').toLowerCase();

  return {
    name: collectionName,
    metadata: {
      description: `Groq-processed chunks from ${processedData.filename}`,
      created_at: new Date().toISOString(),
      total_chunks: processedData.total_chunks,
      llm_used: processedData.document_info.llm_used,
      entities_extracted: processedData.extracted_data.entities ? processedData.extracted_data.entities.length : 0
    }
  };
}}

={{ 
  const processedData = $('Process Results & Create Chunks').first().json;

  return {
    documents: processedData.chunks.map(chunk => chunk.content),
    metadatas: processedData.chunks.map(chunk => ({
      ...chunk.metadata,
      llm_used: processedData.document_info.llm_used,
      chunk_id: chunk.id
    })),
    ids: processedData.chunks.map(chunk => chunk.id)
  };
}}

