// Final response with all results
const fileData = $('Prepare File Path').first().json;
const processedData = $('Process Results & Create Chunks').first().json;
const neo4jResult = $('Store in Neo4j').first().json;
const chromaStoreResult = $('Store Chunks in ChromaDB').first().json;

const response = {
  success: true,
  workflow_type: 'fixed_groq_workflow',
  batch_id: fileData.batch_id,
  processed_file: fileData.filename,
  processing_timestamp: new Date().toISOString(),

  extraction_summary: {
    entities_extracted: processedData.extracted_data.entities?.length || 0,
    relationships_extracted: processedData.extracted_data.relationships?.length || 0,
    text_chunks_created: processedData.total_chunks,
    processing_method: 'groq_http_api_direct'
  },

  storage_summary: {
    neo4j_status: neo4jResult ? 'success' : 'pending',
    chromadb_status: chromaStoreResult ? 'success' : 'pending'
  },

  architecture: {
    llamaparse_node: 'community_node_official',
    ai_extraction: 'groq_api_direct_http_request',
    neo4j_storage: 'http_cypher_transactions',
    chromadb_storage: 'http_api_direct',
    groq_model: 'llama-3.1-70b-versatile',
    workflow_approach: 'http_request_based_fixed'
  }
};

return { json: response };
