// Process LlamaParse response
const webhookData = $('Webhook').first().json;
const llamaParseResult = $input.all()[0];

// Extract markdown content from LlamaParse response
let markdownContent = '';
if (llamaParseResult.json && llamaParseResult.json.markdown) {
  markdownContent = llamaParseResult.json.markdown;
} else if (llamaParseResult.json && typeof llamaParseResult.json === 'string') {
  markdownContent = llamaParseResult.json;
} else if (llamaParseResult.markdown) {
  markdownContent = llamaParseResult.markdown;
} else if (typeof llamaParseResult.json === 'object') {
  markdownContent = JSON.stringify(llamaParseResult.json, null, 2);
} else {
  markdownContent = 'No content extracted from PDF';
}

// Prepare structured response for DocsGPT integration
const response = {
  success: true,
  batch_id: webhookData.batch_id || 'llamaparse-community',
  processed_file: webhookData.filename || 'd4sc03921a.pdf',
  processing_method: 'LlamaParse Community Node',
  markdown_length: markdownContent.length,
  processing_timestamp: new Date().toISOString(),
  status: 'processing_complete',
  markdown_preview: markdownContent.substring(0, 1000) + (markdownContent.length > 1000 ? '...' : ''),
  full_markdown: markdownContent,
  // Additional metadata for knowledge graph storage
  extraction_metadata: {
    node_type: 'llamaparse_community',
    file_path: webhookData.file_path || '/data/local/chemistry-paper.pdf/d4sc03921a.pdf',
    processed_at: new Date().toISOString()
  }
};

return { json: response };
