// Use axios instead of broken n8n HTTP Request binary upload
const fs = require('fs');
const FormData = require('form-data');
const axios = require('axios');

// Get binary data from previous node
const fileBuffer = $binary.data;
const fileName = 'd4sc03921a.pdf';

// Create form data manually
const form = new FormData();
form.append('file', fileBuffer, {
  filename: fileName,
  contentType: 'application/pdf'
});
form.append('result_type', 'markdown');
form.append('premium_mode', 'true');

// Make request with axios
const response = await axios.post('https://api.cloud.llamaindex.ai/api/parsing/upload', form, {
  headers: {
    'Authorization': 'Bearer LLAMAPARSE_API_KEY_PLACEHOLDER',
    ...form.getHeaders()
  },
  timeout: 30000
});

return {
  json: {
    success: true,
    llamaparse_job_id: response.data.id,
    status: 'uploaded',
    file_processed: fileName
  }
};
